mapper.update:
UPDATE PRODUCT
   SET P_AMOUNT = (P_AMOUNT + #{param2})
 WHERE P_NO = #{param1}

mapper.select:
SELECT COUNT(DISTINCT O_IDX)
  FROM ORDERS
 WHERE O_M_NO = #{param1}

mapper.update:
UPDATE PRODUCT
   SET P_AMOUNT = (P_AMOUNT- #{param2})
 WHERE P_NO = #{param1}

UPDATE MEMBERS
   SET M_POINT = (M_POINT - #{param2})
 WHERE M_NO = #{param1}

UPDATE MEMBERS
   SET M_POINT = (M_POINT + #{param2})
 WHERE M_NO = #{param1}

mapper.delete:
DELETE
  FROM CART
 WHERE CRT_M_NO = #{param1}
   AND CRT_P_NO = #{param2}

mapper.select:
SELECT O_P_NO
  FROM ORDERS
 WHERE O_NO = #{param1}

SELECT *
  FROM CART
 WHERE CRT_M_NO = #{param1}
   AND CRT_P_NO = #{param2}

SELECT CRT_P_NO
  FROM CART
 WHERE CRT_M_NO = #{param1}
