mapper.select:
SELECT *
  FROM MEMBERS
 WHERE M_ID = #{param1}
   AND M_PW = #{param2}

SELECT M_ID
  FROM MEMBERS
 WHERE M_ID = #{param1}

	SELECT M_EMAIL
	  FROM MEMBERS
	 WHERE M_EMAIL = #{param2}
   AND NOT M_ID = #{param1}

SELECT M_ID, M_GRADE, M_REGDATE
  FROM MEMBERS
 WHERE M_NAME = #{param1}
   AND M_EMAIL = #{param2}

SELECT *
  FROM MEMBERS
 WHERE M_ID = #{param1}
   AND M_NAME = #{param2}
   AND M_EMAIL = #{param3}

mapper.update:
UPDATE MEMBERS
   SET M_PW = #{param2}
 WHERE M_ID = #{param1}
